import 'dart:html';
// import 'package:csv/csv.dart';

void main() {

  querySelector('#title')?.text = 'what Flag?';

  SelectElement dropdown = SelectElement()..id = 'dropdown';
  OptionElement defaultOption = OptionElement()..text = '--- Select an option ---';
  dropdown.children.add(defaultOption);

  List<String> options = [];

  for (int i = 0; i < countrycode.length; i++) {
    String optionText = '${countrycode[i]}, ${countryname[i]}';
    options.add(optionText);
  }

  for (String optionText in options) {
    OptionElement option = OptionElement()
    ..text = optionText
    ..value = optionText.toString().substring(0,2);
    dropdown.children.add(option);
  }

  DivElement? dropdownContainer = querySelector('#dropcontainer') as DivElement?;

  if (dropdownContainer != null) {
    dropdownContainer.children.add(dropdown);
  }


  // Final Output
  final drop = querySelector("#dropdown") as SelectElement;
  final output = querySelector('#output') as ParagraphElement;
  final outputImage = querySelector('#outputImage') as ImageElement;

  drop.onChange.listen((Event e) {
    String? selectedValue = drop.value;

    var ind = countrycode.indexOf(selectedValue.toString());

    output.text = countryname[ind];  

    if (selectedValue!=null) {
      outputImage.src = countryimg[ind];
      outputImage.alt = countryname[ind];
    }
  });
}

